icUInt8NumbericABtoU8(icFloatNumbernum)
{
icFloatNumberv=num+128.0f;
if(v<0)
v=0;
elseif(v>255)
v=255;

return(icUInt8Number)(v+0.5);
}

icFloatNumbericU8toAB(icUInt8Numbernum)
{
return(icFloatNumber)num-128.0f;
}

ICCPROFLIB_APIicFloatNumbericD50XYZ[3]={0.9642f,1.0000f,0.8249f};
ICCPROFLIB_APIicFloatNumbericD50XYZxx[3]={96.42f,100.00f,82.49f};

voidicNormXyz(icFloatNumber*XYZ,icFloatNumber*WhiteXYZ)
{
if(!WhiteXYZ)
WhiteXYZ=icD50XYZ;

XYZ[0]=XYZ[0]/WhiteXYZ[0];
XYZ[1]=XYZ[1]/WhiteXYZ[1];
XYZ[2]=XYZ[2]/WhiteXYZ[2];
}

voidicDeNormXyz(icFloatNumber*XYZ,icFloatNumber*WhiteXYZ)
{
if(!WhiteXYZ)
WhiteXYZ=icD50XYZ;

XYZ[0]=XYZ[0]*WhiteXYZ[0];
XYZ[1]=XYZ[1]*WhiteXYZ[1];
XYZ[2]=XYZ[2]*WhiteXYZ[2];
}

icFloatNumbericCubeth(icFloatNumberv)
{
if(v>0.008856){
return(icFloatNumber)ICC_CBRTF(v);
}
else{
return(icFloatNumber)(7.787037037037037037037037037037*v+16.0/116.0);
}
}

icFloatNumbericICubeth(icFloatNumberv)
{
if(v>0.20689303448275862068965517241379)
returnv*v*v;
else
#ifndefSAMPLEICC_NOCLIPLABTOXYZ
if(v>16.0/116.0)
#endif
return(icFloatNumber)((v-16.0/116.0)/7.787037037037037037037037037037);
#ifndefSAMPLEICC_NOCLIPLABTOXYZ
else
return0.0;
#endif
}

voidicLabtoXYZ(icFloatNumber*XYZ,consticFloatNumber*Lab/*=NULL*/,consticFloatNumber*WhiteXYZ/*=NULL*/)
{
if(!Lab)
Lab=XYZ;

if(!WhiteXYZ)
WhiteXYZ=icD50XYZ;

icFloatNumberfy=(icFloatNumber)((Lab[0]+16.0)/116.0);

XYZ[0]=icICubeth((icFloatNumber)(Lab[1]/500.0+fy))*WhiteXYZ[0];
XYZ[1]=icICubeth(fy)*WhiteXYZ[1];
XYZ[2]=icICubeth((icFloatNumber)(fy-Lab[2]/200.0))*WhiteXYZ[2];

}

voidicXYZtoLab(icFloatNumber*Lab,consticFloatNumber*XYZ/*=NULL*/,consticFloatNumber*WhiteXYZ/*=NULL*/)
{
icFloatNumberXn,Yn,Zn;

if(!XYZ)
XYZ=Lab;

if(!WhiteXYZ)
WhiteXYZ=icD50XYZ;

Xn=icCubeth(XYZ[0]/WhiteXYZ[0]);
Yn=icCubeth(XYZ[1]/WhiteXYZ[1]);
Zn=icCubeth(XYZ[2]/WhiteXYZ[2]);

Lab[0]=(icFloatNumber)(116.0*Yn-16.0);
Lab[1]=(icFloatNumber)(500.0*(Xn-Yn));
Lab[2]=(icFloatNumber)(200.0*(Yn-Zn));

}

voidicLch2Lab(icFloatNumber*Lab,icFloatNumber*Lch/*=NULL*/)
{
if(!Lch){
Lch=Lab;
}
else
Lab[0]=Lch[0];

icFloatNumbera=(icFloatNumber)(Lch[1]*cos(Lch[2]*PI/180.0));
icFloatNumberb=(icFloatNumber)(Lch[1]*sin(Lch[2]*PI/180.0));

Lab[1]=a;
Lab[2]=b;
}

voidicLab2Lch(icFloatNumber*Lch,icFloatNumber*Lab/*=NULL*/)
{
if(!Lab){
Lab=Lch;
}
else
Lch[0]=Lab[0];

icFloatNumberc=(icFloatNumber)sqrt((double)Lab[1]*Lab[1]+(double)Lab[2]*Lab[2]);
icFloatNumberh=(icFloatNumber)(atan2(Lab[2],Lab[1])*180.0/PI);
while(h<0.0)
h+=360.0;

Lch[1]=c;
Lch[2]=h;
}

icFloatNumbericMin(icFloatNumberv1,icFloatNumberv2)
{
return(v1<v2?v1:v2);
}

icFloatNumbericMax(icFloatNumberv1,icFloatNumberv2)
{
return(v1>v2?v1:v2);
}

icUInt32NumbericIntMin(icUInt32Numberv1,icUInt32Numberv2)
{
return(v1<v2?v1:v2);
}

icUInt32NumbericIntMax(icUInt32Numberv1,icUInt32Numberv2)
{
return(v1>v2?v1:v2);
}


voidicLabFromPcs(icFloatNumber*Lab)
{
Lab[0]*=100.0;
Lab[1]=(icFloatNumber)(Lab[1]*255.0-128.0);
Lab[2]=(icFloatNumber)(Lab[2]*255.0-128.0);
}


voidicLabToPcs(icFloatNumber*Lab)
{
Lab[0]/=100.0;
Lab[1]=(icFloatNumber)((Lab[1]+128.0)/255.0);
Lab[2]=(icFloatNumber)((Lab[2]+128.0)/255.0);
}

voidicXyzFromPcs(icFloatNumber*XYZ)
{
XYZ[0]=(icFloatNumber)(XYZ[0]*65535.0/32768.0);
XYZ[1]=(icFloatNumber)(XYZ[1]*65535.0/32768.0);
XYZ[2]=(icFloatNumber)(XYZ[2]*65535.0/32768.0);
}

voidicXyzToPcs(icFloatNumber*XYZ)
{
XYZ[0]=(icFloatNumber)(XYZ[0]*32768.0/65535.0);
XYZ[1]=(icFloatNumber)(XYZ[1]*32768.0/65535.0);
XYZ[2]=(icFloatNumber)(XYZ[2]*32768.0/65535.0);
}


#defineDUMPBYTESPERLINE16

voidicMemDump(std::string&sDump,void*pBuf,icUInt32NumbernNum)
{
icUInt8Number*pData=(icUInt8Number*)pBuf;
icCharbuf[80]={0};
icCharnum[10]={0};

icInt32Numberi,j;
icUInt8Numberc;

icInt32Numberlines=(nNum+DUMPBYTESPERLINE-1)/DUMPBYTESPERLINE;
sDump.reserve(sDump.size()+lines*79);

for(i=0;i<(icInt32Number)nNum;i++,pData++){
j=i%DUMPBYTESPERLINE;
if(!j){
if(i){
sDump+=(consticChar*)buf;
}
memset(buf,'',76);
buf[76]='';
buf[77]='\n';
buf[78]='\0';
sprintf(num,"%08X:",i);
strncpy(buf,num,9);
}

sprintf(num,"%02X",*pData);
strncpy(buf+10+j*3,num,2);

c=*pData;
if(!isprint(c))
c='.';
buf[10+16*3+1+j]=c;
}
sDump+=buf;
}

voidicMatrixDump(std::string&sDump,icS15Fixed16Number*pMatrix)
{
icCharbuf[128];

sprintf(buf,"%8.4lf%8.4lf%8.4lf\n",icFtoD(pMatrix[0]),icFtoD(pMatrix[1]),icFtoD(pMatrix[2]));
sDump+=buf;
sprintf(buf,"%8.4lf%8.4lf%8.4lf\n",icFtoD(pMatrix[3]),icFtoD(pMatrix[4]),icFtoD(pMatrix[5]));
sDump+=buf;
sprintf(buf,"%8.4lf%8.4lf%8.4lf\n",icFtoD(pMatrix[6]),icFtoD(pMatrix[7]),icFtoD(pMatrix[8]));
sDump+=buf;
}

consticChar*icGet16bitSig(icChar*pBuf,icUInt16NumbernSig,boolbGetHexVal)
{
icUInt16Numbersig=nSig;
icUInt8Numberc;

if(!nSig){
strcpy(pBuf,"NULL");
returnpBuf;
}

pBuf[0]='\'';
c=(icUInt8Number)(sig>>8);
if(!isprint(c))
c='?';
pBuf[1]=c;
c=(icUInt8Number)(sig&0x00FF);
if(!isprint(c))
c='?';
pBuf[2]=c;

if(bGetHexVal)
sprintf(pBuf+3,"'=%04X",nSig);
else
sprintf(pBuf+3,"'");

returnpBuf;
}

consticChar*icGetSig(icChar*pBuf,icUInt32NumbernSig,boolbGetHexVal)
{
inti;
icUInt32Numbersig=nSig;
icUInt8Numberc;

if(!nSig){
strcpy(pBuf,"NULL");
returnpBuf;
}

pBuf[0]='\'';
for(i=1;i<5;i++){
c=(icUInt8Number)(sig>>24);
if(!isprint(c))
c='?';
pBuf[i]=c;
sig<<=8;
}

if(bGetHexVal)
sprintf(pBuf+5,"'=%08X",nSig);
else
sprintf(pBuf+5,"'");

returnpBuf;
}

consticChar*icGetSigStr(icChar*pBuf,icUInt32NumbernSig)
{
inti,j=-1;
icUInt32Numbersig=nSig;
icUInt8Numberc;
boolbGetHexVal=false;

for(i=0;i<4;i++){
c=(icUInt8Number)(sig>>24);
if(!c){
j=i;
}
elseif(j!=-1){
bGetHexVal=true;
}
elseif(!isprint(c)||c==':'){
c='?';
bGetHexVal=true;
}
pBuf[i]=c;
sig<<=8;
}

if(bGetHexVal)
sprintf(pBuf,"%08Xh",nSig);
else
pBuf[4]='\0';

returnpBuf;
}


consticChar*icGetColorSig(icChar*pBuf,icUInt32NumbernSig,boolbGetHexVal)
{
icUInt32Numbersig=nSig;

switch(icGetColorSpaceType(nSig)){
caseicSigNChannelData:
caseicSigReflectanceSpectralData:
caseicSigTransmisionSpectralData:
caseicSigRadiantSpectralData:
caseicSigBiSpectralReflectanceData:
caseicSigSparseMatrixReflectanceData:

pBuf[0]='\"';
pBuf[1]=(icUInt8Number)(sig>>24);
sig<<=8;
pBuf[2]=(icUInt8Number)(sig>>24);
sprintf(pBuf+3,"%04X\"",icNumColorSpaceChannels(nSig));
returnpBuf;

default:
{

inti,j=-1;
icUInt8Numberc;
boolbNeedHexVal=false;

pBuf[0]='\'';
for(i=1;i<5;i++){
c=(icUInt8Number)(sig>>24);
if(!isprint(c)){
c='?';
bNeedHexVal=true;
}
pBuf[i]=c;
sig<<=8;
}

if(bGetHexVal)
sprintf(pBuf+5,"'=%08X",nSig);
elseif(bNeedHexVal){
sprintf(pBuf,"%08Xh",nSig);
}
else
sprintf(pBuf+5,"'");
}
}

returnpBuf;
}


consticChar*icGetColorSigStr(icChar*pBuf,icUInt32NumbernSig)
{
icUInt32Numbersig=nSig;

switch(icGetColorSpaceType(nSig)){
caseicSigNChannelData:
caseicSigReflectanceSpectralData:
caseicSigTransmisionSpectralData:
caseicSigRadiantSpectralData:
caseicSigBiSpectralReflectanceData:
caseicSigSparseMatrixReflectanceData:

pBuf[0]=(icUInt8Number)(sig>>24);
sig<<=8;
pBuf[1]=(icUInt8Number)(sig>>24);
sprintf(pBuf+2,"%04X",icNumColorSpaceChannels(nSig));
returnpBuf;

default:
{

inti,j=-1;
icUInt8Numberc;
boolbGetHexVal=false;

for(i=0;i<4;i++){
c=(icUInt8Number)(sig>>24);
if(!c){
j=i;
}
elseif(j!=-1){
bGetHexVal=true;
}
elseif(!isprint(c)||c==':'){
c='?';
bGetHexVal=true;
}
pBuf[i]=c;
sig<<=8;
}

if(bGetHexVal)
sprintf(pBuf,"%08Xh",nSig);
else
pBuf[4]='\0';
}
}

returnpBuf;
}


std::stringicGetSigPath(icUInt32NumbernSig)
{
charbuf[20];
std::stringrv=":";

rv+=icGetSigStr(buf,nSig);

returnrv;
}

icSignatureicGetFirstSigPathSig(std::stringsigPath)
{
icSignaturerv=0;
constchar*ptr=sigPath.c_str();
while(*ptr==':'){
std::stringsigStr;
ptr++;
while(*ptr&&*ptr!=':'){
sigStr+=*ptr;
ptr++;
}
rv=icGetSigVal(sigStr.c_str());
//Skipembeddedtagpathentry
if(rv!=icSigEmbeddedV5ProfileTag)
break;
}
returnrv;
}

icSignatureicGetLastSigPathSig(std::stringsigPath)
{
size_tn=sigPath.length();
if(!n)
returnicGetSigVal(sigPath.c_str());

constchar*sig=sigPath.c_str();

for(n=n-1;n>0;n--){
if(sig[n]==':'){
break;
}
}
if(/*n>=0&&*/sig[n]==':')
n++;
returnicGetSigVal(sig+n);
}

icSignatureicGetSecondSigPathSig(std::stringsigPath)
{
icSignaturerv=0;
constchar*ptr=sigPath.c_str();
if(*ptr==':'){
std::stringsigStr;
ptr++;
ptr=strchr(ptr+1,':');
if(ptr){
ptr++;
while(*ptr&&*ptr!=':'){
sigStr+=*ptr;
ptr++;
}
rv=icGetSigVal(sigStr.c_str());
}
}
returnrv;
}

icUInt32NumbericGetSigVal(consticChar*pBuf)
{
icUInt32Numberv;

switch(strlen(pBuf)){
case0:
return0;

case1:
return(icUInt32Number)((((unsignedlong)pBuf[0])<<24)+
0x202020);

case2:
return(icUInt32Number)((((unsignedlong)pBuf[0])<<24)+
(((unsignedlong)pBuf[1])<<16)+
0x2020);

case3:
return(icUInt32Number)((((unsignedlong)pBuf[0])<<24)+
(((unsignedlong)pBuf[1])<<16)+
(((unsignedlong)pBuf[2])<<8)+
0x20);

case4:
default:
return(icUInt32Number)((((unsignedlong)pBuf[0])<<24)+
(((unsignedlong)pBuf[1])<<16)+
(((unsignedlong)pBuf[2])<<8)+
(((unsignedlong)pBuf[3])));

case6://Channelbasedcolorsignatures
sscanf(pBuf+2,"%x",&v);

return(icUInt32Number)((((unsignedlong)pBuf[0])<<24)+
(((unsignedlong)pBuf[1])<<16)+
v);

case8:
case9:
sscanf(pBuf,"%x",&v);
returnv;
}
}


icUInt32NumbericGetSpaceSamples(icColorSpaceSignaturesig)
{
switch(sig){
caseicSigGrayData:
caseicSigGamutData:
return1;

caseicSig2colorData:
return2;

caseicSigXYZData:
caseicSigLabData:
caseicSigLuvData:
caseicSigYCbCrData:
caseicSigYxyData:
caseicSigRgbData:
caseicSigHsvData:
caseicSigHlsData:
caseicSigCmyData:
caseicSig3colorData:
caseicSigDevLabData:
caseicSigDevXYZData:
return3;

caseicSigCmykData:
caseicSig4colorData:
return4;

default:
switch(icGetColorSpaceType(sig)){
caseicSigNChannelData:
caseicSigReflectanceSpectralData:
caseicSigTransmisionSpectralData:
caseicSigRadiantSpectralData:
caseicSigBiSpectralReflectanceData:
caseicSigSparseMatrixReflectanceData:
caseicSigSrcMCSChannelData:
returnicNumColorSpaceChannels(sig);

default:
{
icCharszSig[5]={0};
szSig[0]=(icChar)(sig>>24);
szSig[1]=(icChar)(sig>>16);
szSig[2]=(icChar)(sig>>8);
szSig[3]=(icChar)(sig);
szSig[4]='\0';

if(!strcmp(szSig+1,"CLR")){
intd0=icHexDigit(szSig[0]);
if(d0>=0)
returnd0;
}
elseif(!strncmp(szSig,"MCH",3)){//LitteCMSmulti-channelnotation(notpartofICCspecification)
intd0=icHexDigit(szSig[3]);
if(d0>=0)
returnd0;
}
}
return0;
}
}
}

icUInt32NumbericGetSpectralSpaceSamples(consticHeader*pHdr)
{
if(pHdr->spectralPCS)
returnicGetSpaceSamples((icColorSpaceSignature)pHdr->spectralPCS);
else
returnicGetSpaceSamples(pHdr->pcs);
}

icUInt8NumbericGetStorageTypeBytes(icUInt16NumbernStorageType)
{
switch(nStorageType){
caseicValueTypeUInt8:
return1;
caseicValueTypeUInt16:
caseicValueTypeFloat16:
return2;
caseicValueTypeFloat32:
return4;
default:
return0;
}
}


icUInt32NumbericGetMaterialColorSpaceSamples(icMaterialColorSignaturesig)
{
if(icGetColorSpaceType((icColorSpaceSignature)sig)!=icSigSrcMCSChannelData)
return0;

returnicNumColorSpaceChannels(sig);
}

boolicSameSpectralRange(consticSpectralRange&rng1,consticSpectralRange&rng2)
{
return(rng1.start==rng2.start&&
rng1.end==rng2.end&&
rng1.steps==rng2.steps);
}

CIccInfo::CIccInfo():m_szStr{},m_szSigStr{}
{
m_str=newstd::string;
}

CIccInfo::~CIccInfo()
{
deletem_str;
}

consticChar*CIccInfo::GetUnknownName(icUInt32Numberval)
{
icCharbuf[24];
//icGetSig()willprint'NULL'ifvalis0x00000000whichiseasiertopost-process
//if(!val)
//return"Unknown";

sprintf(m_szStr,"Unknown%s",icGetSig(buf,val));

returnm_szStr;
}

consticChar*CIccInfo::GetVersionName(icUInt32Numberval)
{
icFloatNumberver=(icFloatNumber)(((val>>28)&0xf)*10.0+((val>>24)&0xf)+
((val>>20)&0xf)/10.0+((val>>16)&0xf)/100.0);

sprintf(m_szStr,"%.2lf",ver);

returnm_szStr;
}

consticChar*CIccInfo::GetSubClassVersionName(icUInt32Numberval)
{
icFloatNumberver=(icFloatNumber)(((val>>12)&0xf)*10.0+((val>>8)&0xf)+
((val>>4)&0xf)/10.0+(val&0xf)/100.0);

sprintf(m_szStr,"%.2lf",ver);

returnm_szStr;
}

consticChar*CIccInfo::GetDeviceAttrName(icUInt64Numberval)
{
if(val&icTransparency)
strcpy(m_szStr,"Transparency");
else
strcpy(m_szStr,"Reflective");

intl=(int)strlen(m_szStr);

if(val&icMatte)
strcpy(m_szStr+l,"|Matte");
else
strcpy(m_szStr+l,"|Glossy");

returnm_szStr;
}

consticChar*CIccInfo::GetProfileFlagsName(icUInt32Numberval,boolbCheckMCS/*=false*/)
{
if(val&icEmbeddedProfileTrue)
strcpy(m_szStr,"EmbeddedProfileTrue");
else
strcpy(m_szStr,"EmbeddedProfileFalse");

intl=(int)strlen(m_szStr);

if(val&icUseWithEmbeddedDataOnly)
strcpy(m_szStr+l,"|UseWithEmbeddedDataOnly");
else
strcpy(m_szStr+l,"|UseAnywhere");

if(bCheckMCS){
l=(int)strlen(m_szStr);

if(val&icMCSNeedsSubsetTrue)
strcpy(m_szStr+l,"|MCSNeedsSubset");
else
strcpy(m_szStr+l,"|MCSConnectAny");
}

if(val&icExtendedRangePCS)
strcpy(m_szStr+l,"|ExtendedRangePCS");


returnm_szStr;
}

