std::stringCIccInfo::GetSigPathName(std::stringsigPath)
{
std::stringrv;
constchar*ptr=sigPath.c_str();
intn=0;
while(*ptr==':'){
std::stringsigStr;
ptr++;
while(*ptr&&*ptr!=':'){
sigStr+=*ptr;
ptr++;
}
icSignaturesig=icGetSigVal(sigStr.c_str());
if(n!=0){
rv+=">";
}
rv+=GetPathEntrySigName(sig);
n++;
}
if(n>0)
rv+=":";

returnrv;
}


