return"MCH2Data/2ColorData";

caseicSigMCH3Data:
return"MCH3Data/3ColorData";

caseicSigMCH4Data:
return"MCH4Data/4ColorData";

caseicSigMCH5Data:
return"MCH5Data/5ColorData";

caseicSigMCH6Data:
return"MCH6Data/6ColorData";

caseicSigMCH7Data:
