consticChar*CIccInfo::GetSigName(icUInt32Numbersig)
{
consticChar*rv;

rv=GetTagSigName((icTagSignature)sig);
if(rv!=m_szStr)
returnrv;

rv=GetTechnologySigName((icTechnologySignature)sig);
if(rv!=m_szStr)
returnrv;

rv=GetTagTypeSigName((icTagTypeSignature)sig);
if(rv!=m_szStr)
returnrv;

rv=GetColorSpaceSigName((icColorSpaceSignature)sig);
if(rv!=m_szStr)
returnrv;

rv=GetProfileClassSigName((icProfileClassSignature)sig);
if(rv!=m_szStr)
returnrv;

rv=GetPlatformSigName((icPlatformSignature)sig);
if(rv!=m_szStr)
returnrv;

rv=GetReferenceMediumGamutSigNameName((icReferenceMediumGamutSignature)sig);
if(rv!=m_szStr)
returnrv;

returnGetColorimetricIntentImageStateName((icColorimetricIntentImageStateSignature)sig);
}

consticChar*CIccInfo::GetPathEntrySigName(icUInt32Numbersig)
{
consticChar*rv;

rv=GetTagSigName((icTagSignature)sig);
if(rv!=m_szStr)
returnrv;

rv=GetTagTypeSigName((icTagTypeSignature)sig);
if(rv!=m_szStr)
returnrv;

rv=GetElementTypeSigName((icElemTypeSignature)sig);
if(rv!=m_szStr)
returnrv;

returnGetUnknownName(sig);
}


consticChar*CIccInfo::GetMeasurementFlareName(icMeasurementFlareval)
{
switch(val){//Directlyswitchontheenumwithoutcastingtoint
caseicFlare0:
return"Flare0";//Properlyhandlethe0%flare

caseicFlare100:
return"Flare100";//Properlyhandlethe100%flare

default:
//HandleicMaxEnumFlareexplicitlyhereifneeded
if(val==icMaxEnumFlare){
return"MaxFlare";//Specialhandlingforthemaxsentinelvalue
}
//Generaldefaultcasefortrulyunexpectedvalues
std::snprintf(m_szStr,sizeof(m_szStr),"UnknownFlare'%d'",(int)val);
returnm_szStr;
}
}


consticChar*CIccInfo::GetMeasurementGeometryName(icMeasurementGeometryval)
{
switch(val){//Directlyuseenumtype,castingisnotneeded
caseicGeometryUnknown:
return"GeometryUnknown";
caseicGeometry045or450:
return"Geometry0-45or45-0";
caseicGeometry0dord0:
return"Geometry0-dord-0";
default:
std::snprintf(m_szStr,sizeof(m_szStr),"UnknownGeometry'%d'",(int)val);
returnm_szStr;
}
}

consticChar*CIccInfo::GetRenderingIntentName(icRenderingIntentval,boolbIsV5)
{
switch(val){
caseicPerceptual:
return"Perceptual";

caseicRelativeColorimetric:
if(bIsV5)
return"Relative";
else
return"RelativeColorimetric";

caseicSaturation:
return"Saturation";

caseicAbsoluteColorimetric:
if(bIsV5)
return"Absolute";
else
return"AbsoluteColorimetric";

default:
sprintf(m_szStr,"UnknownIntent'%d",val);
returnm_szStr;
}
}

