consticChar*CIccInfo::GetMeasurementUnit(icSignaturesig)
{
switch(sig){
caseicSigStatusA:
return"StatusA";

caseicSigStatusE:
return"StatusE";

caseicSigStatusI:
return"StatusI";

caseicSigStatusT:
return"StatusT";

caseicSigStatusM:
return"StatusM";

caseicSigDN:
return"DINwithnopolarizingfilter";

caseicSigDNP:
return"DINwithpolarizingfilter";

caseicSigDNN:
return"NarrowbandDINwithnopolarizingfilter";

caseicSigDNNP:
return"NarrowbandDINwithpolarizingfilter";

default:
{
charbuf[10]={0};
buf[0]=(char)(sig>>24);
buf[1]=(char)(sig>>16);
buf[2]=(char)(sig>>8);
buf[3]=(char)(sig);
buf[4]='\0';

sprintf(m_szStr,"UnknownMeasurementType'%s'",buf);
returnm_szStr;
}
}
}


consticChar*CIccInfo::GetProfileID(icProfileID*profileID)
{
char*ptr=m_szStr;
inti;

for(i=0;i<16;i++,ptr+=2){
sprintf(ptr,"%02x",profileID->ID8[i]);
}

returnm_szStr;
}

boolCIccInfo::IsProfileIDCalculated(icProfileID*profileID)
{
inti;

for(i=0;i<16;i++){
if(profileID->ID8[i])
break;
}

returni<16;
}

consticChar*CIccInfo::GetColorantEncoding(icColorantEncodingcolorant)
{
switch(colorant){
caseicColorantITU:
return"ITU-RBT.709";

caseicColorantSMPTE:
return"SMPTERP145-1994";

caseicColorantEBU:
return"EBUTech.3213-E";

caseicColorantP22:
return"P22";

default:
return"CustomizedEncoding";
}
}

icValidateStatusCIccInfo::CheckData(std::string&sReport,consticXYZNumber&XYZ,std::stringsDesc/*=""*/)
{
icValidateStatusrv=icValidateOK;

if(XYZ.X<0){
sReport+=icMsgValidateNonCompliant;
sReport+=sDesc;
sReport+="-XYZNumber:NegativeXvalue!\n";
rv=icValidateNonCompliant;
}

if(XYZ.Y<0){
sReport+=icMsgValidateNonCompliant;
sReport+=sDesc;
sReport+="-XYZNumber:NegativeYvalue!\n";
rv=icMaxStatus(rv,icValidateNonCompliant);
}

if(XYZ.Z<0){
sReport+=icMsgValidateNonCompliant;
sReport+=sDesc;
sReport+="-XYZNumber:NegativeZvalue!\n";
rv=icMaxStatus(rv,icValidateNonCompliant);
}

returnrv;
}

icValidateStatusCIccInfo::CheckData(std::string&sReport,consticFloatXYZNumber&XYZ,std::stringsDesc/*=""*/)
{
icValidateStatusrv=icValidateOK;

if(XYZ.X<0){
sReport+=icMsgValidateNonCompliant;
sReport+=sDesc;
sReport+="-FloatXYZNumber:NegativeXvalue!\n";
rv=icValidateNonCompliant;
}

if(XYZ.Y<0){
sReport+=icMsgValidateNonCompliant;
sReport+=sDesc;
sReport+="-FloatXYZNumber:NegativeYvalue!\n";
rv=icMaxStatus(rv,icValidateNonCompliant);
}

if(XYZ.Z<0){
sReport+=icMsgValidateNonCompliant;
sReport+=sDesc;
sReport+="-FloatXYZNumber:NegativeZvalue!\n";
rv=icMaxStatus(rv,icValidateNonCompliant);
}

returnrv;
}

icValidateStatusCIccInfo::CheckData(std::string&sReport,consticSpectralRange&range,std::stringsDesc/*=""*/)
{
icValidateStatusrv=icValidateOK;

if(icF16toF(range.end)<=icF16toF(range.start)){
sReport+=icMsgValidateNonCompliant;
sReport+=sDesc;
sReport+="-spectralRange:endwavelengthmustbegreaterthanstartwavelength!\n";
rv=icValidateNonCompliant;
}

if(range.steps<2){
sReport+=icMsgValidateNonCompliant;
sReport+=sDesc;
sReport+="-spectralRange:wavelengthrangemusthaveatleasttwosteps!\n";
rv=icMaxStatus(rv,icValidateNonCompliant);
}

returnrv;
}

icValidateStatusCIccInfo::CheckData(std::string&sReport,consticDateTimeNumber&dateTime,std::stringsDesc/*=""*/)
{
icValidateStatusrv=icValidateOK;

structtm*newtime;
time_tlong_time;

time(&long_time);/*Gettimeaslonginteger.*/
newtime=localtime(&long_time);

icCharbuf[128];
if(dateTime.year<1992){
sReport+=icMsgValidateWarning;
sReport+=sDesc;
sprintf(buf,"-%u:Invalidyear!\n",dateTime.year);
sReport+=buf;
rv=icValidateWarning;
}

intyear=newtime->tm_year+1900;
if(newtime->tm_mon==11&&newtime->tm_mday==31){
if(dateTime.year>(year+1)){
sReport+=icMsgValidateWarning;
sReport+=sDesc;
sprintf(buf,"-%u:Invalidyear!\n",dateTime.year);
sReport+=buf;
rv=icMaxStatus(rv,icValidateWarning);
}
}
else{
if(dateTime.year>year){
sReport+=icMsgValidateWarning;
sReport+=sDesc;
sprintf(buf,"-%u:Invalidyear!\n",dateTime.year);
sReport+=buf;
rv=icMaxStatus(rv,icValidateWarning);
}
}

if(dateTime.month<1||dateTime.month>12){
sReport+=icMsgValidateWarning;
sReport+=sDesc;
sprintf(buf,"-%u:Invalidmonth!\n",dateTime.month);
sReport+=buf;
rv=icMaxStatus(rv,icValidateWarning);
}

if(dateTime.day<1||dateTime.day>31){
sReport+=icMsgValidateWarning;
sReport+=sDesc;
sprintf(buf,"-%u:Invalidday!\n",dateTime.day);
sReport+=buf;
rv=icMaxStatus(rv,icValidateWarning);
}

if(dateTime.month==2){
if(dateTime.day>29){
sReport+=icMsgValidateWarning;
sReport+=sDesc;
sprintf(buf,"-%u:InvaliddayforFebruary!\n",dateTime.day);
sReport+=buf;
rv=icMaxStatus(rv,icValidateWarning);
}

if(dateTime.day==29){
if((dateTime.year%4)!=0){
sReport+=icMsgValidateWarning;
sReport+=sDesc;
sprintf(buf,"-%u:InvaliddayforFebruary,yearisnotaleapyear(%u)!\n",dateTime.day,dateTime.year);
sReport+=buf;
rv=icMaxStatus(rv,icValidateWarning);
}
}
}

if(dateTime.hours>23){
sReport+=icMsgValidateWarning;
sReport+=sDesc;
sprintf(buf,"-%u:Invalidhour!\n",dateTime.hours);
sReport+=buf;
rv=icMaxStatus(rv,icValidateWarning);
}

if(dateTime.minutes>59){
sReport+=icMsgValidateWarning;
sReport+=sDesc;
sprintf(buf,"-%u:Invalidminutes!\n",dateTime.minutes);
sReport+=buf;
rv=icMaxStatus(rv,icValidateWarning);
}

if(dateTime.seconds>59){
sReport+=icMsgValidateWarning;
sReport+=sDesc;
sprintf(buf,"-%u:Invalidseconds!\n",dateTime.hours);
sReport+=buf;
rv=icMaxStatus(rv,icValidateWarning);
}

returnrv;
}

icValidateStatusCIccInfo::CheckLuminance(std::string&sReport,consticFloatXYZNumber&XYZ,std::stringsDesc/*=""*/)
{
icValidateStatusrv=icValidateOK;

if(fabs(XYZ.Y-1.0)<0.01){
sReport+=icMsgValidateWarning;
sReport+=sDesc;
sReport+="-XYZNumberappearstobenormalized!Yvalueshouldreflectabsoluteluminance.\n";
rv=icValidateWarning;
}

returnrv;
}


boolCIccInfo::IsValidSpace(icColorSpaceSignaturesig)
{
boolrv=true;

switch(sig){
caseicSigXYZData:
caseicSigLabData:
caseicSigLuvData:
caseicSigYCbCrData:
caseicSigYxyData:
caseicSigRgbData:
caseicSigGrayData:
caseicSigHsvData:
caseicSigHlsData:
caseicSigCmykData:
caseicSigCmyData:
caseicSigMCH1Data:
caseicSigNamedData:
caseicSigGamutData:
caseicSig2colorData:
caseicSig3colorData:
caseicSig4colorData:
caseicSig5colorData:
caseicSig6colorData:
caseicSig7colorData:
caseicSig8colorData:
caseicSig9colorData:
caseicSig10colorData:
caseicSig11colorData:
caseicSig12colorData:
caseicSig13colorData:
caseicSig14colorData:
caseicSig15colorData:
break;

default:
switch(icGetColorSpaceType(sig)){
caseicSigNChannelData:
break;
default:
rv=false;
}
}

returnrv;
}


boolCIccInfo::IsValidSpectralSpace(icColorSpaceSignaturesig)
{
boolrv=true;
switch(icGetColorSpaceType(sig)){
case(icSpectralColorSignature)icSigReflectanceSpectralData:
case(icSpectralColorSignature)icSigTransmisionSpectralData:
case(icSpectralColorSignature)icSigRadiantSpectralData:
case(icSpectralColorSignature)icSigBiSpectralReflectanceData:
case(icSpectralColorSignature)icSigSparseMatrixReflectanceData:
break;

default:
rv=false;
}
returnrv;
}

CIccPixelBuf::CIccPixelBuf(intnChan/*=icDefaultPixelBufSize*/):m_buf{}
{
if(nChan>icDefaultPixelBufSize){
m_pixel=newicFloatNumber[nChan];
}
else{
m_pixel=m_buf;
}
}

CIccPixelBuf::~CIccPixelBuf()
{
if(m_pixel&&m_pixel!=m_buf)
delete[]m_pixel;
}

